function ExitComponent(backendId, componentId) {
  this.backendId = backendId;
  this.componentId = componentId;
}

ExitComponent.prototype.getBackendId = function () {
  return this.backendId;
}

ExitComponent.prototype.getExitComponentAsStr = function () {
  return this.componentId.toString();
}

module.exports.ExitComponent = ExitComponent;