'use strict';

function Transaction(args) {
  this.error = args.error;
  this.ms = args.ms;
  this.ts = args.ts;
  this.guid = args.guid;
  this.accountGUID = args.accountGUID;
  this.applicationID = args.applicationID;
  this.tierID = args.tierID;
  this.startedExitCalls = args.startedExitCalls;
  this.exitCalls = args.exitCalls;
  this.name = args.name;
  this.entryType = args.entryType;
  this.registrationId = args.registrationId;
  this.isAutoDiscovered = args.isAutoDiscovered;
  this.hasErrors = args.hasErrors;
  this.ignore = args.ignore;
  this.exitCallCounter = 0;
  this.corrHeader = args.corrHeader;
  this.eumGuid = args.eumGuid;
  this.stopped = args.stopped;
  this.callerChain = args.callerChain ? args.callerChain : '';
}

exports.Transaction = Transaction;
