'use strict';

var events = require('./events');

function ErrorEvent(args) {
    this.event_type = events.ERROR_EVENT,
    this.bt_id = args.bt_id;
    this.backend_id = args.backend_id;
    this.caller_chain = args.caller_chain;
    this.error_name = args.error_name;
    this.error_message = args.error_message;
    this.version = args.version;
    this.http_status_code = args.http_status_code;
    this.start_time = args.start_time;
    this.end_time = args.end_time;
    this.transaction_guid = args.transaction_guid;
    this.entry_point_name = args.entry_point_name;
    this.entry_point_type = args.entry_point_type;
}

exports.ErrorEvent = ErrorEvent;