'use strict';

var events = require('./events');

function BTRegistrationEvent(entryPointName, entryPointType, tracerVersion) {
    this.event_type = events.REGISTRATION_BT_EVENT;
    this.entry_point_name = entryPointName;
    this.entry_point_type = entryPointType;
    this.version = tracerVersion;
}

exports.BTRegistrationEvent = BTRegistrationEvent;