function ExitForeignAppComponent(backendId, foreignAppId) {
  this.backendId = backendId;
  this.foreignAppId = foreignAppId;
}

ExitForeignAppComponent.prototype.getBackendId = function () {
  return this.backendId;
}

ExitForeignAppComponent.prototype.getExitComponentAsStr = function () {
  return 'A' + this.foreignAppId;
}

module.exports.ExitForeignAppComponent = ExitForeignAppComponent;